                $('#pincode').html('<option value="">Select Pincode</option>');            }        });    });</script><script>    var placeSearch, autocomplete;    var componentForm = {        postal_code: 'short_name'    };    function initAutocomplete() {        autocomplete = new google.maps.places.Autocomplete(            document.getElementById('autocomplete'), {types: ['geocode']});        autocomplete.setFields(['address_component']);        //autocomplete.addListener('place_changed', fillInAddress);    }    function geolocate() {        if (navigator.geolocation) {            navigator.geolocation.getCurrentPosition(function(position) {                var geolocation = {                    lat: position.coords.latitude,                    lng: position.coords.longitude                };                var circle = new google.maps.Circle(                    {center: geolocation, radius: position.coords.accuracy});                autocomplete.setBounds(circle.getBounds());            });        }    }</script><script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyAKwyqrGsG1Stuy5UpUBoldcWEtAIeJMOU&libraries=places&callback=initAutocomplete"        async defer></script>